/** ************************************************************************* * Copyright (c) 2000: * University of Alberta, * Deptartment of Computing Science * Computer Poker Research Group * * See "Liscence.txt" ************************************************************************** */package org.alberta.poker.ai.model;import org.alberta.poker.ai.Context;import org.alberta.poker.ai.ProbTriple;/** * An interface to define an action predictor system. * * @author Aaron Davidson * @version 1.1.0 */public interface Predictor {    /**     * Load any needed data from a file     */    public void load(String fname);    /**     * Given a context, predict an action     *     * @param c the context in which the action is to be made.     * @param takeMax predict maximum likely action, or else choose from a     * distribution.     */    public ProbTriple predictAction(Context c);    /**     * Give a training example of an observed action to the predictor.     */    public void observeAction(Context c);    /**     * Save any needed data to a file.     */    public void save(String fname);}