/** ************************************************************************* * Copyright (c) 2000: * University of Alberta, * Deptartment of Computing Science * Computer Poker Research Group * * See "Liscence.txt" ************************************************************************** */package org.alberta.poker.util;import java.util.Random;/** * Looks after random number generation. * * @author Aaron Davidson * @version 1.1.0 */public class Randomizer extends Random {    Random rnd = new Random();    private long extra = 0;    /**     * Generate a random integer from 0 to range-1     *     * @param range where to pick a random number from     * @return the random number     */    public int randInt(int range) {        return ((int) extra + (int) (this.nextDouble() * range)) % range;    }    public double nextDouble() {        if (extra++ % 2 == 0) {            return super.nextDouble();        }        return rnd.nextDouble();    }    public void setExtraSeed(long sd) {        rnd.setSeed(sd);        extra = sd;    }    public void test() {        int size = 25;        int[][] grid = new int[size][size];        for (int i = 0; i < size * size * size * size; i++) {            grid[randInt(size)][randInt(size)]++;        }        for (int i = 0; i < size; i++) {            for (int j = 0; j < size; j++) {                System.out.print(grid[i][j] + " ");            }            System.out.println("");        }    }}